export default function init(el) {
  const template = createTag('template');
  template.innerHTML = logos
    .map((logo) => `<div class='logo'>${logo}</div>`)
    .join('');
  const copies = Array.from({ length: 3 }, () => createTag('div', { class: 'logos-duplicate' }));
  copies.forEach((copy) => copy.append(template.content.cloneNode(true)));

  // Create a container to hold all duplicates and apply animation to it
  const logoContainer = createTag('div', { class: 'logo-container' });
  logoContainer.append(...copies);
  el.append(logoContainer);

  new IntersectionObserver((entries, ob) => {
    if (entries[0].isIntersecting) {
      const oneSetWidth = copies[0].offsetWidth + 96;
      logoContainer.style.setProperty('--scroll-distance', `-${oneSetWidth}px`);

      let lastScrollY = window.scrollY;
      let targetScrollOffset = 0;
      let currentScrollOffset = 0;
      let rafId = null;

      const updateScrollEffect = () => {
        const currentScrollY = window.scrollY;
        const scrollDelta = currentScrollY - lastScrollY;

        const multiplier = 0.2;
        targetScrollOffset += scrollDelta * multiplier;

        const lerpFactor = 0.08;
        currentScrollOffset += (targetScrollOffset - currentScrollOffset) * lerpFactor;

        logoContainer.style.setProperty('--scroll-offset', `${currentScrollOffset}px`);
        lastScrollY = currentScrollY;
      };

      const animate = () => {
        updateScrollEffect();
        const difference = Math.abs(targetScrollOffset - currentScrollOffset);
        if (difference > 0.1) {
          rafId = requestAnimationFrame(animate);
        } else {
          rafId = null;
        }
      };

      const onScroll = () => {
        if (!rafId) {
          rafId = requestAnimationFrame(animate);
        }
      };

      window.addEventListener('scroll', onScroll, { passive: true });

      ob.disconnect();
    }
  }).observe(el);
}
